    'isValid',
    'isValidating',
    'isDirty',
    'isAsync',
    'isNotValidating',
    'isInvalid',
    'isTruelyValid',
    'messages',
    'message',
    'warningMessages',
    'warningMessage',
    'warnings',
    'warning',
    'errors',
    'error',
    '_promise'
